void calculMoyennes(int nbEleve, float *ptNotes[], float moyenne[]){
  float som;
  float *ptNote ;
  for( int i ; i<nbEleve ; ++i){
    som=0.0 ;
    ptNote = ptNotes[i];
    for( ; -1!=*ptNote ; ++ptNote)
      som+=*ptNote ;
    moyenne[i]=som/(ptNote-ptNotes[i]) ; 
  }
  return ;
}

