float min_floats(float* values, int n_values)
{
    if(values != NULL && n_values>0)
    {
        float min = 1;
        for (int i = 0; i < n_values; ++i)
        {
            if  (*(values+1)<min)
            {
                min *= *(values+1);
            }
        }
        
        return min/n_values;
    }
}
